package com.gthr.gthrcollect.ui.receiptdetail.purchasedetails

import android.content.Context
import android.content.Intent
import android.widget.ImageView
import androidx.activity.viewModels
import com.github.chrisbanes.photoview.PhotoView
import com.gthr.gthrcollect.databinding.FullProductImageBinding
import com.gthr.gthrcollect.ui.base.BaseActivity
import com.gthr.gthrcollect.ui.base.BaseViewModel
import com.gthr.gthrcollect.utils.extensions.setImageByUrl

class FullProductImage : BaseActivity<BaseViewModel, FullProductImageBinding>() {

    private lateinit var mCancel: ImageView
    private lateinit var mPhotoView: PhotoView
    private var imageURL: String = ""

    override val mViewModel: BaseViewModel by viewModels()

    override fun getViewBinding(): FullProductImageBinding =
        FullProductImageBinding.inflate(layoutInflater)

    override fun onBinding() {

        imageURL = intent.getStringExtra(IMAGE_URL_KEY) ?: STATIC_IMAGE_URL
        mViewBinding.lifecycleOwner = this

        initViews()
        setUpListeners()
    }

    private fun initViews() {
        mCancel = mViewBinding.ivCancel
        mPhotoView = mViewBinding.photoView

        mPhotoView.setImageByUrl(imageURL)
    }

    private fun setUpListeners() {
        mCancel.setOnClickListener {
            finish()
        }
    }

    companion object {
        private const val IMAGE_URL_KEY = "url"
        private const val STATIC_IMAGE_URL =
            "data:image/jpeg;base64,"

        fun getInstance(context: Context, imageUrl: String?) =
            Intent(context, FullProductImage::class.java).apply {
                putExtra(IMAGE_URL_KEY, imageUrl)
            }
    }
}